from nose.tools import with_setup, ok_

from hommod.controllers.clustal import clustal_aligner
from hommod.controllers.storage import model_storage
from hommod.models.template import TemplateID
from hommod.default_settings import CLUSTALW_EXE


def setup():
    model_storage.model_dir = "tests/integration/data/models"
    clustal_aligner.clustalw_exe = CLUSTALW_EXE


def teardown():
    pass


@with_setup(setup, teardown)
def test_skip_unaligned():
    models = model_storage.list_models("",
                                       'HUMAN', 428, TemplateID('2J4Y', 'A'))
    ok_(len(models) <= 0)


@with_setup(setup, teardown)
def test_pick_aligned():
    models = model_storage.list_models("",
                                       'HUMAN', 389, TemplateID('2J4Y', 'A'))
    ok_(len(models) > 0)

